-- ----------------------------
-- Table structure for fa_circle
-- ----------------------------
DROP TABLE IF EXISTS `fa_circle`;
CREATE TABLE `fa_circle` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `pid` int(11) DEFAULT '0' COMMENT '父ID',
  `title` varchar(128) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '名称',
  `titlesub` varchar(250) CHARACTER SET utf8 DEFAULT '' COMMENT '小标题',
  `iconimage` varchar(255) CHARACTER SET utf8 DEFAULT '' COMMENT '图标',
  `weigh` int(11) unsigned DEFAULT '0' COMMENT '排序',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态:0=审核中,1=正常,2=拒绝',
  `user_id` int(10) DEFAULT '1' COMMENT '管理员',
  `bannerimage` varchar(255) CHARACTER SET utf8 DEFAULT '' COMMENT '幻灯',
  `follow_num` int(10) DEFAULT '0' COMMENT '关注数',
  `recswitch` tinyint(1) DEFAULT '0' COMMENT '推荐',
  `zan_num` int(11) DEFAULT '0' COMMENT '点赞数',
  `post_num` int(10) DEFAULT '0' COMMENT '帖子数',
  `hotswitch` tinyint(1) DEFAULT '0' COMMENT '热门',
  `payswitch` tinyint(1) DEFAULT '0' COMMENT '付费',
  `bg` varchar(255) CHARACTER SET utf8 DEFAULT '' COMMENT '背景颜色',
  `desc` varchar(500) DEFAULT '' COMMENT '圈子描述',
  `privpostdata` enum('everyone','admin') DEFAULT 'everyone' COMMENT '发帖模式:全员、管理员可发',
  `privcomdata` enum('everyone','forbid') DEFAULT 'everyone' COMMENT '评论模式:全员可评，禁止评论',
  `createtime` int(11) DEFAULT NULL COMMENT '创建时间',
  `updatetime` int(11) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `title_key` (`title`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8mb4 COMMENT='圈子';

