-- ----------------------------
-- Table structure for fa_posts
-- ----------------------------
DROP TABLE IF EXISTS `fa_posts`;
CREATE TABLE `fa_posts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `images` text COMMENT '帖子图片',
  `user_id` int(10) DEFAULT '0' COMMENT '用户',
  `circle_id` int(10) DEFAULT '0' COMMENT '所属圈子',
  `type` enum('text','video','image','gif','poll','audio','link') DEFAULT 'text' COMMENT '帖子类型',
  `ip_city` varchar(50) DEFAULT NULL COMMENT '城市',
  `file_url` varchar(500) DEFAULT '' COMMENT '视频/音频地址',
  `media_width` int(10) DEFAULT '0' COMMENT '视频宽',
  `media_height` int(10) DEFAULT '0' COMMENT '视频高',
  `duration` int(10) DEFAULT '0' COMMENT '音视频时长',
  `poll_data` text COMMENT '投票数据',
  `like_num` int(10) DEFAULT '0' COMMENT '点赞数',
  `com_num` int(10) DEFAULT '0' COMMENT '评论数',
  `view_num` int(10) DEFAULT '0' COMMENT '浏览数',
  `recswitch` tinyint(1) DEFAULT '0' COMMENT '推荐',
  `topswitch` tinyint(1) DEFAULT '0' COMMENT '置顶',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态:0=审核中,1=正常,2=拒绝',
  `content` longtext CHARACTER SET utf8mb4 COMMENT '详情',
  `updatetime` int(14) DEFAULT '0' COMMENT '修改时间',
  `createtime` int(14) DEFAULT '0' COMMENT '申请时间',
  `deletetime` int(10) DEFAULT NULL COMMENT '是否删除',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=40 DEFAULT CHARSET=utf8 COMMENT='圈子帖子';

